var updated_format = d3.timeFormat('%b. %d %Y %I:%M %p')
var color = d3.scaleLinear()
    .domain([0, 50, 100])
    .range(['#0091FF', 'white', '#FF6060']);

queue()
    .defer(d3.csv, 'https://data.jhkforecasts.com/2020-presidential.csv')
    .defer(d3.csv, 'https://data.jhkforecasts.com/2020-senate.csv')
    .defer(d3.csv, 'https://data.jhkforecasts.com/2020-house.csv')
    .defer(d3.json, 'https://projects.jhkforecasts.com/presidential-forecast/us-states.json')
    .await(ready)

function ready(error, pres, senate, house, json) {
    if (error) throw error
    var pres = pres.filter(d => d.party == 'gop')
    var prestoday = pres.slice(pres.length - 57, pres.length)
    var trumpWin = +pres[pres.length - 1].win

    presInfo.forEach((d, i) => {
        var state = d.state
        d.win = +prestoday.filter(d => d.state == state)[0].win
    })
    console.log(presInfo)

    var bubblemap = d3.select('#bubblemap')
        .append('svg')
        .attr('viewBox', '-150 0 1000 450')

    bubblemap
        .append('text')
        .text('Donald TRUMP')
        .attr('x', '830')
        .attr('y', 20)
        .attr('fill', d => 'black')
        .attr('text-anchor', 'end')
        .attr('dominant-baseline', 'central')
        .attr('font-size', 20)
        .style('font-weight', '100')
        .style('font-family', 'sf-mono')

    bubblemap
        .append('text')
        .text(nf(trumpWin) + '%')
        .attr('x', '830')
        .attr('y', 50)
        .attr('fill', colors[0])
        .attr('text-anchor', 'end')
        .attr('dominant-baseline', 'central')
        .attr('font-size', 25)
        .style('font-weight', '100')
        .style('font-family', 'sf-mono')


    bubblemap
        .append('text')
        .text(nf(100 - trumpWin) + '%')
        .attr('x', '-130')
        .attr('y', 50)
        .attr('fill', colors[1])
        .attr('text-anchor', 'start')
        .attr('dominant-baseline', 'central')
        .attr('font-size', 25)
        .style('font-weight', '100')
        .style('font-family', 'sf-mono')

    bubblemap
        .append('text')
        .text('Joseph Biden')
        .attr('x', '-130')
        .attr('y', 20)
        .attr('fill', d => 'black')
        .attr('text-anchor', 'start')
        .attr('dominant-baseline', 'central')
        .attr('font-size', 20)
        .style('font-weight', '100')
        .style('font-family', 'sf-mono')

    bubblemap.selectAll('circ')
        .data(presInfo)
        .enter()
        .append('circle')
        .attr('cx', d => d.x)
        .attr('cy', d => d.y)
        .attr('r', d => d.radius)
        .attr('fill', d => color(d.win))

    bubblemap.selectAll('labels')
        .data(presInfo)
        .enter()
        .append('text')
        .text(d => d.label)
        .attr('x', d => d.x)
        .attr('y', d => d.y)
        .attr('fill', d => Math.abs(50 - d.win) > 15 ? 'white' : 'black')
        .attr('text-anchor', 'middle')
        .attr('dominant-baseline', 'central')
        .attr('font-size', 8)
        .style('font-weight', '500')
        .style('font-family', 'sf-mono')

    bubblemap.selectAll('circ')
        .data(presInfo)
        .enter()
        .append('a')
        .attr('href', '#statespres')
        .append('circle')
        .attr('class', 'statesover')
        .attr('cx', d => d.x)
        .attr('cy', d => d.y)
        .attr('r', d => d.radius)
        .on('click', function (d) {
            presState(d.state)
        })

    function presState(state) {

        d3.select('.presChange').remove()
        var stateData = pres.filter(d => d.state == state)
        var svg = d3.select('#statespres')
            .append('svg')
            .attr('class', 'presChange')
            .attr('viewBox', '0 0 1000 200')
        console.log(stateData)

        svg.append('text')
            .text(state)
            .attr('x', '500')
            .attr('y', 30)
            .attr('fill', d => 'black')
            .attr('text-anchor', 'middle')
            .attr('dominant-baseline', 'central')
            .attr('font-size', 30)
            .style('font-weight', '100')
            .style('font-family', 'sf-mono')

        svg
            .append('text')
            .text('Donald TRUMP')
            .attr('x', '980')
            .attr('y', 30)
            .attr('fill', d => 'black')
            .attr('text-anchor', 'end')
            .attr('dominant-baseline', 'central')
            .attr('font-size', 20)
            .style('font-weight', '100')
            .style('font-family', 'sf-mono')

        svg
            .append('text')
            .text(nf(stateData[stateData.length - 1].win) + '%')
            .attr('x', '980')
            .attr('y', 60)
            .attr('fill', colors[0])
            .attr('text-anchor', 'end')
            .attr('dominant-baseline', 'central')
            .attr('font-size', 25)
            .style('font-weight', '100')
            .style('font-family', 'sf-mono')


        svg
            .append('text')
            .text(nf(100 - stateData[stateData.length - 1].win) + '%')
            .attr('x', '20')
            .attr('y', 60)
            .attr('fill', colors[1])
            .attr('text-anchor', 'start')
            .attr('dominant-baseline', 'central')
            .attr('font-size', 25)
            .style('font-weight', '100')
            .style('font-family', 'sf-mono')

        svg
            .append('text')
            .text('Joseph Biden')
            .attr('x', '20')
            .attr('y', 30)
            .attr('fill', d => 'black')
            .attr('text-anchor', 'start')
            .attr('dominant-baseline', 'central')
            .attr('font-size', 20)
            .style('font-weight', '100')
            .style('font-family', 'sf-mono')

        var dayWidth = 900 / 247
        var x = d3.scaleTime()
            .rangeRound([50, 950])
            .domain([new Date(2020, 2, 1), new Date(2020, 10, 3)])

        svg.append("g")
            .attr("class", "x-axis")
            .attr("transform", "translate(0,160)")
            .call(d3.axisBottom(x).tickSize(-520).ticks(5)
                .tickFormat(d3.timeFormat("%b")))
            .call(g => {
                var years = x.ticks(d3.timeYear.every(1))
                var xshift = 0
                g.selectAll("text")
                    .style("text-anchor", "right")
                    .attr("y", 15)
                    .attr('fill', 'black')
                    .attr('font-size', 15)
                    .style('font-weight', 100)
                g.selectAll("line")
                    .attr("opacity", 0)
                    .attr("stroke", "grey")


                g.select(".domain")
                    .attr("opacity", 0)


            })

        svg.selectAll('sda')
            .data(stateData)
            .enter()
            .append("rect")
            .attr('width', dayWidth)
            .attr('height', 50)
            .attr('y', 100)
            .attr('x', (d, i) => 50 + i * dayWidth)
            .attr("fill", d => color(d.win))
            .attr("stroke", d => color(d.win))
    }

    var senatemap = d3.select("#senatemap")
        .append("svg")
        .attr("viewBox", '0 0 1000 400')


    var projection = d3.geoAlbersUsa()
        .scale([800])
        .translate([1000 / 2, 400 / 2]);

    var path = d3.geoPath()
        .projection(projection);

    json.features.forEach((d, i) => {
        var state = d.properties.name
        d.properties.abbrev = presInfo.filter(d => d.state == state).length == 0 ? "" : presInfo.filter(d => d.state == state)[0].label
    })



    senatemap
        .selectAll("labels")
        .data(json.features)
        .enter()
        .append("text")
        .attr("x", function (d) { return path.centroid(d)[0] })
        .attr("y", function (d) { return path.centroid(d)[1] })
        .text(function (d) { return d.properties.abbrev })
        .attr("text-anchor", "middle")
        .attr("alignment-baseline", "central")
        .style("font-size", 11)
        .style("fill", "black")



}
