var color = d3.scaleLinear()
    .domain([-5, 0, 5])
    .range(["#0091FF", "white", "#FF6060"]),
    dataformat = d3.format(".1f")
d3.csv("https://data.jhkforecasts.com/2020-presidential.csv", data => {
    console.log(data)
    var newest_data = data.slice(data.length - 171, data.length)
    var sd = []
    for (let k = 0; k < map_labels.length; k++) {
        var dt = newest_data.filter(d => d.state == map_labels[k].state)
        var ml = map_labels.filter(d => d.state == map_labels[k].state)
        var finaldt = {
            state: map_labels[k].state,
            electoral_votes: +dt[0].electoral_vote,
            gop_win: +dt[0].win,
            dem_win: +dt[1].win,
            third_win: +dt[2].win,
            poll_margin: +dt[0].proj_vote - +dt[1].proj_vote,
            fund_margin: +dt[0].experts_avg - +dt[1].experts_avg,
            third_vote: +dt[2].proj_vote,
            tipping_point: +dt[0].tipping_point,
            x_value: ml[0].xValue,
            y_value: ml[0].yValue,
            label: ml[0].label,
        }
        finaldt.margin = finaldt.gop_vote - finaldt.dem_vote
        sd.push(finaldt)
    }

    var map = d3.select("#usmap")
        .append("svg")
        .attr("viewBox", '150 0 750 470');

    var width3 = 1020;
    var height3 = 500;

    var projection = d3.geoAlbersUsa()
        .translate([width3 / 2, height3 / 2])
        .scale([900]);


    var path = d3.geoPath()
        .projection(projection);

    var tool_tip = d3.tip()
        .attr("class", "d3-tip")
        .offset([-180, -90])
        .html("<div id='tipDiv'></div>");

    map.call(tool_tip);
    d3.json("https://projects.jhkforecasts.com/presidential_forecast/us-states.json", function (json) {

        for (var i = 0; i < sd.length; i++) {

            var dataState = sd[i].state;
            var value = sd[i].poll_margin == 0 ? 0 : sd[i].poll_margin - sd[i].fund_margin;
            var xv = map_labels[i].xValue
            var yv = map_labels[i].yValue
            var label = map_labels[i].label
            var region = map_labels[i].region
            var bg = map_labels[i].battleground

            for (var j = 0; j < json.features.length; j++) {
                var jsonState = json.features[j].properties.name;

                if (dataState == jsonState) {
                    json.features[j].properties.yv = yv
                    json.features[j].properties.label = label
                    json.features[j].properties.xv = xv
                    json.features[j].properties.region = region
                    json.features[j].properties.value = value
                    json.features[j].properties.bg = bg
                    break;
                }
            }
        }
        var mapdata = json.features
        var battleground = mapdata.filter(d => d.properties.bg == "y")
        console.log(mapdata)

        map.append("text")
            .text("Polling Average - Fundamental Average")
            .attr("x", 525)
            .attr("y", 30)
            .attr("font-size", "20")
            .attr("fill", "black")
            .attr("text-anchor", "middle")
            .style("font-weight", "800")
        map.append("g")
            .selectAll("path2")
            .data(mapdata)
            .enter()
            .append("path")
            .attr("d", path)
            .style("stroke", "#AFAFAF")
            .style("stroke-width", 1)
            .style("fill", d => "white")

        map.append("g")
            .selectAll("path2")
            .data(battleground)
            .enter()
            .append("path")
            .attr("d", path)
            .style("stroke", "#AFAFAF")
            .style("stroke-width", 1)
            .style("fill", d => color(d.properties.value))

        map.selectAll("label")
            .data(battleground)
            .enter()
            .append("text")
            .text(d => d.properties.label)
            .attr("x", d => d.properties.xv)
            .attr("y", d => d.properties.yv - 5)
            .style("font-family", "source-code-pro")
            .attr("font-size", "10")
            .attr("fill", "black")
            .attr("text-anchor", "middle")
            .style("font-weight", "500")

        map.selectAll("label")
            .data(battleground)
            .enter()
            .append("text")
            .text(d => d.properties.value == 0 ? "" : dataformat(d.properties.value))
            .attr("x", d => d.properties.xv)
            .attr("y", d => d.properties.yv + 5)
            .attr("font-size", "10")
            .attr("fill", "black")
            .attr("text-anchor", "middle")
            .style("font-weight", "500")

    })
})